                       function GpDataTable(id){
                           $(id).DataTable(
                                   {    dom: 'Bfrtip', 
                                        buttons: ['print'],
                                        "paging": true,
                                        "lengthChange": false,
                                        "searching": true,
                                        "ordering": true,
                                        "info": false,
                                        "autoWidth": true,
                                        "language": {"paginate": {"previous": "Précédent", "next": "Suivant"}, "search": "Rechercher: ",
                                            "emptyTable": "Aucune information", "zeroRecords": "Aucune correspondance trouvée à votre recherche... Merci!!!."}
                                   }
                                    );
                       }
                       
                      
